/********************  Boaz formating start **********************/

body {
  max-width: 2570px;
  margin: auto;
}

body,
input,
textarea,
button {
  font-family: 'Montserrat', sans-serif;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  width: 13px;
  background: white;
  border-left: 0 solid white;
  border-right: 0 solid white;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(100deg, #001647, #001647, #001647);
  background-size: 180% 180%;
  width: 0;
  height: 25%;
  animation: gradient-animation 5s ease infinite;
}

*,
:after,
:before {
  box-sizing: border-box;
}

h2,
h3,
h5 {
  margin: 0;
}

a {
  text-decoration: none;
}

p {
  font-size: 1rem;
  font-weight: 400;
}

button {
  background-color: transparent;
  border: 0;
}

ul {
  list-style: none;
}

.cursor {
  cursor: pointer;
}

.d-flex {
  display: flex;
}

.grid-2,
.grid-3,
.grid-7 {
  display: grid;
}

.grid-2 {
  grid-template-columns: repeat(2, 1fr);
}

.grid-3 {
  grid-template-columns: repeat(3, 1fr);
}

.grid-7 {
  grid-template-columns: repeat(7, 1fr);
}

.float-right {
  float: right;
}

.position-relative {
  position: relative;
}

.position-absolute {
  position: absolute;
}

.text-transform-uppercase {
  text-transform: uppercase;
}

.t-end {
  text-align: end;
}

.text-center {
  text-align: center;
}

.map {
  -webkit-filter: grayscale(100%);
  -moz-filter: grayscale(100%);
  -ms-filter: grayscale(100%);
  -o-filter: grayscale(100%);
  filter: grayscale(100%);
}

.animate-in {
  opacity: 1 !important;
  transform: translateX(0) !important;
}

.bg-color-w {
  background-color: #fff;
}

.text-color {
  color: #000;
}

.text-color-w {
  color: #fff;
}

.linear-font {
  background: linear-gradient(100deg, #001647, #155b9b, #001647);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
  -webkit-text-fill-color: transparent;
  background-size: 180% 180%;
  animation: gradient-animation 3s ease infinite;
}

/********************   Animation formating start **********************/

@keyframes gradient-animation {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

/********************   Animation formating end **********************/

/********************  Identical instructions start **********************/

.page__header {
  width: 19%;
  padding: 0.7em 2.5em 0.7em 5em;
  margin: 1.5em 0 3em 0;
  text-align: end;
}

.page__header--right {
  margin: 0;
  padding: 0;
  position: absolute;
  right: 0;
  top: -2.5%;
  padding: 0.7em 5em 0.7em 2.5em;
  width: max-content;
}

.header-bottom,
.page__header,
.services-content,
.page-link,
.footer-content__link-box,
.page-btn {
  background: #001647;
}

.page__header__h2,
.page__header__h3,
.service-box__title {
  font-weight: 700;
}

.page-link,
.page-btn {
  padding: 0.5em 1.5em;
}

.menu__item,
.page-link,
.footer-link,
.page-btn {
  font-weight: 600;
}

.page-link__img {
  width: 9.5%;
  top: 0.15em;
}

.slider-content__img,
.service-bottom__img,
.blog-box__img,
.form-group__input,
.form-group__textarea {
  width: 100%;
}

.slider-content__img,
.service-bottom__img {
  height: 100%;
}

.blog-box__text,
.footer-content__form {
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

/********************  Identical instructions end **********************/

/********************  Header formating start **********************/

.header-content {
  padding: 1em 5em;
}

.header-content,
.header-content__nav-box,
.header-bottom {
  align-items: center;
}

.header-content__logo-box {
  width: 20%;
}

.header-content__nav-box {
