sourcesContent:
/********************  Boaz formating start **********************/

body {
  max-width: 2570px;
  margin: auto;
}

body,
input,
textarea,
button {
  font-family: 'Montserrat', sans-serif;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  width: 13px;
  background: white;
  border-left: 0 solid white;
  border-right: 0 solid white;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(100deg, #001647, #001647, #001647);
  background-size: 180% 180%;
  width: 0;
  height: 25%;
  animation: gradient-animation 5s ease infinite;
}

*,
:after,
:before {
  box-sizing: border-box;
}

h2,
h3,
h5 {
  margin: 0;
}

a {
  text-decoration: none;
}

p {
  font-size: 1rem;
  font-weight: 400;
}

button {
  background-color: transparent;
  border: 0;
}

ul {
  list-style: none;
}

.cursor {
  cursor: pointer;
}

.d-flex {
  display: flex;
}

.grid-2,
.grid-3,
.grid-7 {
  display: grid;
}

.grid-2 {
  grid-template-columns: repeat(2, 1fr);
}

.grid-3 {
  grid-template-columns: repeat(3, 1fr);
}

.grid-7 {
  grid-template-columns: repeat(7, 1fr);
}

.float-right {
  float: right;
}

.position-relative {
  position: relative;
}

.position-absolute {
  position: absolute;
}

.text-transform-uppercase {
  text-transform: uppercase;
}

.t-end {
  text-align: end;
}

.text-center {
  text-align: center;
}

.map {
  -webkit-filter: grayscale(100%);
  -moz-filter: grayscale(100%);
  -ms-filter: grayscale(100%);
  -o-filter: grayscale(100%);
  filter: grayscale(100%);
}

.animate-in {
  opacity: 1 !important;
  transform: translateX(0) !important;
}

.bg-color-w {
  background-color: #fff;
}

.text-color {
  color: #000;
}

.text-color-w {
  color: #fff;
}

.linear-font {
  background: linear-gradient(100deg, #001647, #155b9b, #001647);
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
  -webkit-text-fill-color: transparent;
  background-size: 180% 180%;
  animation: gradient-animation 3s ease infinite;
}

/********************   Animation formating start **********************/

@keyframes gradient-animation {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

/********************   Animation formating end **********************/

/********************  Identical instructions start **********************/

.page__header {
  width: 19%;
  padding: 0.7em 2.5em 0.7em 5em;
  margin: 1.5em 0 3em 0;
  text-align: end;
}

.page__header--right {
  margin: 0;
  padding: 0;
  position: absolute;
  right: 0;
  top: -2.5%;
  padding: 0.7em 5em 0.7em 2.5em;
  width: max-content;
}

.header-bottom,
.page__header,
.services-content,
.page-link,
.footer-content__link-box,
.page-btn {
  background: #001647;
}

.page__header__h2,
.page__header__h3,
.service-box__title {
  font-weight: 700;
}

.page-link,
.page-btn {
  padding: 0.5em 1.5em;
}

.menu__item,
.page-link,
.footer-link,
.page-btn {
  font-weight: 600;
}

.page-link__img {
  width: 9.5%;
  top: 0.15em;
}

.slider-content__img,
.service-bottom__img,
.blog-box__img,
.form-group__input,
.form-group__textarea {
  width: 100%;
}

.slider-content__img,
.service-bottom__img {
  height: 100%;
}

.blog-box__text,
.footer-content__form {
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

/********************  Identical instructions end **********************/

/********************  Header formating start **********************/

.header-content {
  padding: 1em 5em;
}

.header-content,
.header-content__nav-box,
.header-bottom {
  align-items: center;
}

.header-content__logo-box {
  width: 20%;
}

.header-content__nav-box {
  width: 80%;
}

.header-content__logo-box__link__img {
  width: 70%;
}

.menu__list {
  list-style: none;
  padding: 0;
  margin: 0;
  justify-content: end;
}

.menu__item,
.header-bottom__div__h1,
.header-bottom__div-linkbox__link {
  font-size: 0.95rem;
}

.menu__item {
  padding-left: 2.5em;
}

.menu__item--border-left {
  border-left: 0.12rem solid #000;
}

.header-bottom {
  justify-content: space-between;
  padding: 0.7em 5em;
}

.header-bottom__div__h1 {
  font-weight: 700;
}

.header-bottom__div__span,
.footer-content__link-box__DigitalSeed__Nuxtlink {
  font-weight: 400;
}

.header-bottom__div-linkbox__link__img {
  width: 20px;
  height: 20px;
  margin-right: 0.5em;
  top: 0.25em;
}

.header-bottom__div-linkbox__link__img--margin-left {
  margin-left: 2.3em;
}

/********************  Header formating end **********************/

/********************  Slider formating start **********************/

/********************  Slider formating end **********************/

/********************  Partners formating start **********************/

.partners-content {
  padding: 0 0 5em 0;
}

.partners-content__img-box__div__img {
  width: 90%;
  height: 50%;
}

.partners-content__img-box {
  padding: 0 2em 0 4em;
}

/********************  Partners formating end **********************/

/********************  Services formating start **********************/

.services-content {
  padding: 8.5em 5em 3.5em 5em;
}

.servive-grid--margin-bottom {
  margin-bottom: 6.5em;
}

.service-box {
  margin: 0.5em;
  padding: 5em 1.5em 1.5em 1.5em;
  height: 20em;
}

.service-box__title {
  margin: 2em 0 0 0;
}

.service-box__img {
  width: 33%;
  display: block;
  margin: auto;
  margin-top: -43%;
}

.service-bottom__box {
  top: 40%;
  right: 5em;
  padding: 0 2em;
  width: max-content;
}

.service-bottom__box__title {
  font-size: 2.5rem;
  margin: 0.7em 0;
}

.service-box__description {
  margin-bottom: 3em;
}

.service-box__link-box {
  bottom: 3em;
  left: 34%;
  width: 34%;
}

/********************  Services formating end **********************/

/********************  Blog formating start **********************/

.blog-box {
  margin: 0 0.5em;
}

.blog-grid {
  padding: 1em 5em;
}

.blog-box__text {
  height: 14.7em;
  padding: 1.5em;
  margin-top: -5px;
}

.blog-box__text__h3 {
  width: 90%;
}

.blog-box__text,
.blog-box__link-box__Nuxtlink,
.footer-content__link-box__DigitalSeed__Nuxtlink {
  color: #001647;
}

.page__header--margin-top {
  margin-top: -1.9em;
  position: relative;
}

.blog-box__link-box {
  bottom: 1.2em;
}

.blog-box__link-box__Nuxtlink {
  font-weight: 500;
  text-decoration: underline;
}

/********************  Blog formating end **********************/

/********************  Footer formating start **********************/

.footer-content {
  background: #e2efff;
  padding-top: 3.5em;
}

.footer-content__form {
  margin: 0 5em;
  padding: 3em 6em 3em 6em;
}

.footer-content__form__h5 {
  margin-bottom: 3em;
}

.footer-content__link-box {
  margin: 7em 13em 0 13em;
}

.footer-link {
  color: #fff;
  padding-left: 2.5em;
}

.footer-content__link-box__DigitalSeed {
  margin-top: 1em;
  padding: 0.7em;
}

.page-btn {
  padding: 0.9em 1.5em;
  cursor: pointer;
  width: 13%;
}

.page-btn__img {
  width: 20%;
  top: 0.15em;
  margin-left: 0.3em;
}

.form-group__input,
.form-group__textarea {
  border: 1px solid #001647;
  padding: 1em;
  margin: 0.7em 0;
}

.form-group--margin-right {
  margin-right: 0.5em;
}

.form-group--margin-left {
  margin-left: 0.5em;
}

.footer-content__link-box__img {
  width: 12.5%;
  margin: -3.5em 0 3.5em 0;
}

.footer-link:first-child {
  padding-left: 0;
}

.footer-content__link-box__links {
  justify-content: center;
}
/********************  Footer formating end **********************/

/********************  Media formating start **********************/

@media (min-width: 2200px) and (max-width: 2570px) {
  p {
    font-size: 1.4rem;
  }
  .menu__item,
  .header-bottom__div__h1,
  .header-bottom__div-linkbox__link {
    font-size: 1.5rem;
  }
  .header-bottom__div-linkbox__link__img {
    width: 25px;
    height: 25px;
  }
  .menu__list__li__img {
    width: 2em;
    margin-top: -0.5em;
  }
  .page__header__h2,
  .page__header__h3,
  .service-box__title,
